bool isPalindrome(String input) {
  final normalized = input.toLowerCase().replaceAll(RegExp(r'\s+'), '');
  return normalized == normalized.split('').reversed.join();
}

int fib(int n) {
  if (n <= 1) return n;
  var a = 0, b = 1;
  for (var i = 2; i <= n; i++) {
    final t = a + b;
    a = b;
    b = t;
  }
  return b;
}
