int max(int[] values) {
  int max = values[0];
  for (int v : values) {
    if (v > max) max = v;
  }
  return max;
}

List<Integer> unique(int[] values) {
  Set<Integer> seen = new HashSet<>();
  List<Integer> out = new ArrayList<>();
  for (int v : values) {
    if (seen.add(v)) out.add(v);
  }
  return out;
}

boolean isPrime(int n) {
  if (n < 2) return false;
  for (int i = 2; i * i <= n; i++) {
    if (n % i == 0) return false;
  }
  return true;
}

int[] rotate(int[] arr, int k) {
  int n = arr.length;
  k %= n;
  int[] out = new int[n];
  for (int i = 0; i < n; i++) {
    out[(i + k) % n] = arr[i];
  }
  return out;
}
